def cnt(i, j, k):
	if i < j:
		i += j
		j = i - j
		i -= j
	if j < k:
		j += k
		k = j - k
		j -= k
	ans = 0
	for l in range(i + 1, n):
		ans += cnt(i, j, l)
	for l in range(j + 1, n):
		ans += cnt(i, l, k)
	for l in range(k + 1, n):
		ans += cnt(l, j, k)
	return ans

def solve():
	ans = 0
	for i in range(n):
		for j in range(i + 1, n):
			for k in range(j + 1, n):
				ans += cnt(i, j, k)
	return ans
