def prob():
	from collections import Counter
	import re
	import math
	S = input()
	T = input()
	S = re.sub('\+', '1', S)
	S = re.sub('-', '-1', S)
	T = re.sub('\+', '1', T)
	T = re.sub('-', '-1', T)
	T = re.sub('\?', '0', T)
	T = [int(T[i]) for i in range(len(T))]
	S = [int(S[i]) for i in range(len(S))]
	S = [str(S[i]) for i in range(len(S))]
	S = ''.join(S)
	T = ''.join(T)
	S = S.split('0')
	T = T.split('0')
	C = Counter(T)
	ans = 0
	for i in range(len(S)):
		if S[i] in C:
			ans += math.pow(0.5, len(S[i]) - 1)
	ans = ans / 2
	return ans
