import sys
from math import factorial as f

def comb(n, k):
	return f(n) / (f(n - k) * f(k))

def calc(s, x):
	y = x
	res = 0.0
	for i in s:
		if i == '+':
			x += 1
		else:
			x -= 1
		if x == y:
			res += 1
	return res / len(s)

def solve(s, t):
	res = 0.0
	for i in range(len(t)):
		if t[i] == '?':
			res += calc(s, i)
		elif t[i] == '-':
			s = s[:i] + '-' + s[i:]
		elif t[i] == '+':
			s = s[:i] + '+' + s[i:]
	return res

