def dfs(node, g, visited, parent):
	(visited[node], parent[node]) = (1, parent)
	for neighbour in g[node]:
		if visited[neighbour]:
			if neighbour != parent:
				return True
		else:
			if dfs(neighbour, g, visited, node):
				return True
	return False

