class Solution:

	def __init__(self):
		self.files = set()
		self.files_dict = {}
		self.ans = []

	def solve(self, n, files, q, queries):
		self.files = set(files)
		for file in self.files:
			self.files_dict[file] = len(file)
		for query in queries:
			self.ans.append(self.query(query))
		return self.ans

	def query(self, query):
		query_len = len(query)
		if query_len == 1:
			if query[0] == '.':
				return (len(self.files), next(iter(self.files)))
			else:
				return (sum((file.count(query) for file in self.files)), next(((file for file in self.files if file.count(query)))))
		elif query_len == 2 and query[0] == '.':
			return (sum((file.count(query) for file in self.files)), next(((file for file in self.files if file.count(query)))))
		else:
			return (sum((file.count(query) for file in self.files)), next(((file for file in self.files if file.count(query)))))

