def f(x, y):
	if x % y == 0:
		return x // y
	return x // y + 1

def g(x, y):
	return x // y

def solve(p, q):
	l = 1
	r = 10 ** 9
	while l < r:
		m = (l + r + 1) // 2
		if f(p, m) * g(q, m) <= f(p, m - 1) * g(q, m - 1):
			r = m - 1
		else:
			l = m
	return l
