def is_black(x, y):
	return (x + y) % 2 == 0


def is_white(x, y):
	return not is_black(x, y)


def is_valid(x, y):
	return 0 <= x < n and 0 <= y < n


def is_good(x, y):
	if is_black(x, y) and a[0][x][y] == 0:
		return False
	if is_white(x, y) and a[0][x][y] == 1:
		return False
	return True


def bfs(x, y):
	q = deque([(x, y)])
	while q:
		(x, y) = q.popleft()
		for (dx, dy) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
			nx = x + dx
			ny = y + dy
			if is_valid(nx, ny) and is_good(nx, ny):
				a[0][nx][ny] = a[0][x][y] ^ 1
				q.append((nx, ny))


