import sys
input = sys.stdin.readline

def main():
	(n, m) = map(int, input().split())
	graph = [set() for _ in range(n + 1)]
	for _ in range(m):
		(x, y) = map(int, input().split())
		graph[x].add(y)
		graph[y].add(x)
	group = [0] * (n + 1)
	visited = [False] * (n + 1)
	ans = []

	def dfs(x):
		visited[x] = True
		group[x] = len(ans)
		ans.append(x)
		for y in graph[x]:
			if visited[y]:
				if group[y] == group[x]:
					print(-1)
					sys.exit()
			else:
				dfs(y)
	for i in range(1, n + 1):
		if not visited[i]:
			dfs(i)
	print(len(ans) // 3)
	for i in range(0, len(ans), 3):
		print(ans[i], ans[i + 1], ans[i + 2])
main()


