	n = len(mat)
	q = deque([(x, y)])
	vis = [[0] * n for _ in range(n)]
	vis[x][y] = 1
	while q:
		(a, b) = q.popleft()
		for (dx, dy) in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
