def solve(a, b, k):
	n = len(a)
	d = [0] * n
	for i in range(n):
		for j in range(i + 1, n):
			d[i] = max(d[i], dist(a[i], b[i], a[j], b[j]))
	h = []
	for (i, v) in enumerate(d):
