def cross_prime(n, k):
	cross = []
	for i in range(n):
		cross.append([])
		for j in range(n):
			cross[i].append(0)
	for i in range(n):
		for j in range(n):
			cross[i][j] = (i + 1) * (j + 1)
	cross_prime = []
	for i in range(n):
		for j in range(n):
			if check_prime(cross[i][j]):
				cross_prime.append((i + 1, j + 1))
	cross_prime.sort()
	cross_prime_k = []
	for i in range(len(cross_prime)):
		if cross_prime[i][0] + k <= n and cross_prime[i][1] + k <= n:
			cross_prime_k.append(cross_prime[i])
	cross_prime_k.sort()
	for i in range(len(cross_prime_k)):
		if check_prime(cross_prime_k[i][0] + k) and check_prime(cross_prime_k[i][1] + k):
			cross_prime_k[i] = (cross_prime_k[i][0] + k, cross_prime_k[i][1] + k)
			return cross_prime_k
	cross_prime_k.sort()
	for i in range(len(cross_prime_k)):
		if check_prime(cross_prime_k[i][0] - k) and check_prime(cross_prime_k[i][1] - k):
			cross_prime_k[i] = (cross_prime_k[i][0] - k, cross_prime_k[i][1] - k)
			return cross_prime_k
	return None

