	(a, b) = map(int, sys.stdin.readline().split())
	adj[a].append(b)
	adj[b].append(a)
dp = [[0 for i in range(N)] for j in range(1 << N)]
for i in range(N):
	dp[1 << i][i] = 1
for mask in range(1, 1 << N):
	for i in range(N):
		if (mask & 1 << i) != 0:
			for j in adj[i + 1]:
				if (mask & 1 << j - 1) != 0:
					dp[mask][j - 1] += dp[mask - 1 << i][i]
					dp[mask][j - 1] %= mod
ans = 0
for i in range(N):
	ans += dp[1 << N - 1][i]
	ans %= mod
print(ans)

