def check(n):
	if n[0] == '0' and len(n) != 1:
		return False
	if len(n) > 12:
		return False
	return True

def main():
	n = int(input())
	arr = []
	for i in range(n):
		s = input()
		arr.append(s)
	arr.sort(key=len)
	ans = 'unlucky'
	for i in range(n):
		s = arr[i]
		if len(s) > 12:
			break
		arr.pop(i)
		for j in range(len(s)):
			a = s[0:j]
			if not check(a):
				continue
			for k in range(j, len(s)):
				b = s[j:k]
				if not check(b):
					continue
				for l in range(k, len(s)):
					c = s[k:l]
					if not check(c):
						continue
					d = s[l:]
					if not check(d):
						continue
					arr.sort(reverse=True)
					if int(a) + int(b) + int(c) + int(d) > int(ans):
						ans = str(int(a) + int(b) + int(c) + int(d))
		arr.insert(i, s)
	print(ans)
for _ in range(int(input())):
	main()

