def max_val(s):
	res = [0] * 4
	i = 0
	while i < len(s):
		j = i
		while j < len(s) and len(res) > 0:
			j += 1
			if s[i:j].lstrip('0'):
				res.pop()
				res.append(int(s[i:j]))
				i = j
				break
		else:
			break
	return sum(res)
