def g(a, b, c, d):
	return f(a) + f(b) + f(c) + f(d)

def h(s):
	for i in range(1, len(s)):
		for j in range(i + 1, len(s)):
			for k in range(j + 1, len(s)):
				for l in range(k + 1, len(s)):
					yield (s[:i], s[i:j], s[j:k], s[k:l])
					yield (s[:i], s[i:j], s[j:l], s[k:l])
					yield (s[:i], s[i:k], s[j:k], s[k:l])
					yield (s[:i], s[i:k], s[j:l], s[k:l])
					yield (s[:i], s[i:l], s[j:l], s[k:l])
					yield (s[:j], s[i:j], s[j:k], s[k:l])
					yield (s[:j], s[i:j], s[j:l], s[k:l])
					yield (s[:j], s[i:k], s[j:k], s[k:l])
					yield (s[:j], s[i:k], s[j:l], s[k:l])
					yield (s[:j], s[i:l], s[j:l], s[k:l])
					yield (s[:k], s[i:k], s[j:k], s[k:l])
					yield (s[:k], s[i:k], s[j:l], s[k:l])
					yield (s[:k], s[i:l], s[j:l], s[k:l])
					yield (s[:l], s[i:l], s[j:l], s[k:l])
