def solve(n, a):
	ans = [[0] * n for _ in range(n)]

	def bfs(i, j, c):
		q = deque([(i, j)])
		while q:
			i, j = q.popleft()
			if 0 <= i < n and 0 <= j < n and (not ans[i][j]):
				ans[i][j] = c
				q.append((i - 1, j))
				q.append((i, j - 1))

	for i in range(n):
		for j in range(n):
			if not ans[i][j] and a[i][j] == 1:
				bfs(i, j, 1)
	return ans
