def f(n, a):
	if n == 1:
		return 1
	d = defaultdict(int)
	for i in range(n):
		d[a[i]] += 1
	if d[1] > 0:
		return d[1]
	ans = n + 1
	for i in range(n):
		if a[i] % 2 == 0:
			ans = min(ans, f(i, a[:i]) + f(n - i - 1, a[i + 1:]))
		for j in range(i + 1, n):
			if a[i] % a[j] == 0 and check(i, j, a):
				ans = min(ans, f(i, a[:i]) + f(n - j - 1, a[j + 1:]))
	return ans

