class GCD:

	def __init__(self, n, a):
		self.n = n
		self.a = a
		self.c = [0] * 1000000001
		self.p = [0] * (n + 1)
		self.ans = 0
		self.f = [0] * (n + 1)
		self.dp = [0] * (n + 1)
		self.c[0] = 1
		self.p[0] = 1

	def solve(self):
		for i in range(1, self.n):
			self.c[self.a[i]] += 1
		for i in range(1, 1000000001):
			self.c[i] += self.c[i - 1]
		for i in range(1, self.n):
			self.p[self.c[self.a[i]]] = i
			self.c[self.a[i]] -= 1
		for i in range(1, self.n):
			if self.p[i] < self.p[i - 1]:
				self.f[i] = self.f[i - 1] + 1
			else:
				self.f[i] = self.f[i - 1]
		for i in range(1, self.n):
			self.dp[i] = self.dp[i - 1] + self.f[i]
		self.ans = self.dp[self.n - 1]
		for i in range(1, self.n):
			self.ans = min(self.ans, self.dp[i - 1] + self.n - self.f[i] + self.f[self.p[self.n - i]])
		print(self.ans)


