from heapq import heappop, heappush

def shortestPath(n, edges):
	G = [[] for _ in range(n)]
	for (s, t, w) in edges:
		G[s].append((t, w))
	Q = [(0, 0)]
	d = [float('inf')] * n
	while Q:
		(d0, u) = heappop(Q)
		if d0 < d[u]:
			d[u] = d0
			for (v, w) in G[u]:
				heappush(Q, (d0 + w, v))
	return d[-1] if d[-1] < float('inf') else -1

def shortest_path(N, edge_list):
	(graph, weight) = ({i: {} for i in range(N)}, {i: float('inf') for i in range(N)})
	for (s, e, w) in edge_list:
		graph[s][e] = w
	weight[0] = 0
	stack = [0]
	while stack:
		node = stack.pop(0)
		for (next_node, w) in graph[node].items():
			if weight[next_node] > weight[node] + w:
				weight[next_node] = weight[node] + w
				stack.append(next_node)
	return weight[N - 1] if weight[N - 1] < float('inf') else -1


