(n, m) = map(int, input().split())
s = [input() for _ in range(n)]
x = [0] * n
for i in range(n):
	x[i] = s[i].find('*')
p = [0] * n
for i in range(n):
	p[i] = s[i].find('#')
q = [0] * n
for i in range(n):
	q[i] = s[i].find('&')
for i in range(n):
	a = min(x[i], p[i], q[i])
	b = max(x[i], p[i], q[i])
	print(min(b, m - a))
