def solve(num, k):
	if k == 0:
		return int(num)
	if k > len(num):
		return -1
	if num[0] == '0':
		return -1
	ans = -1
	for i in range(1, len(num) - k + 2):
		temp = solve(num[i:], k - 1)
		if temp == -1:
			continue
		ans = max(ans, int(num[:i]) + temp)
	return ans

