def cost(i, j, x, y, board, dp):
	n = len(board)
	m = len(board[0])
	if i == x and j == y:
		return 0
	if dp[i][j][x][y] != -1:
		return dp[i][j][x][y]
	ans = 10000
	if board[i][j] == 'U' and i > 0:
		ans = min(ans, 1 + cost(i - 1, j, x, y, board, dp))
	if board[i][j] == 'D' and i < n - 1:
		ans = min(ans, 1 + cost(i + 1, j, x, y, board, dp))
	if board[i][j] == 'L' and j > 0:
		ans = min(ans, 1 + cost(i, j - 1, x, y, board, dp))
	if board[i][j] == 'R' and j < m - 1:
		ans = min(ans, 1 + cost(i, j + 1, x, y, board, dp))
	dp[i][j][x][y] = ans
	return ans

