def solve(board, n, m, k):
	dirs = {'U': (0, -1), 'L': (-1, 0), 'D': (0, 1), 'R': (1, 0)}
	dp = [[[-1] * 4 for i in range(m)] for j in range(n)]
	dp[0][0] = [0, 0, 0, 0]
	q = [(0, 0, 0)]
	while q:
		(i, j, t) = q.pop()
		if t >= k:
			return dp[i][j][t]
		for (di, dj) in dirs.values():
			ni = i + di
			nj = j + dj
			if 0 <= ni < n and 0 <= nj < m:
				nt = t + 1
				nd = dp[i][j][t]
				if board[ni][nj] == '*':
					if dp[ni][nj][nt] == -1:
						dp[ni][nj][nt] = nd
						q.append((ni, nj, nt))
				else:
					nx = ord(board[ni][nj]) - ord('A')
					nd = dp[i][j][t] | (1 << nx)
					if dp[ni][nj][nt] == -1:
						dp[ni][nj][nt] = nd
						q.append((ni, nj, nt))
	return -1

def coinOnTheTable(m, k, board):
	n = len(board)
	return solve(board, n, m, k)


