import sys

def solve(n, m, k, b):
	dp = [[[0] * m for _ in range(n)] for _ in range(k + 1)]
	dp[0][0][0] = 1
	for t in range(1, k + 1):
		for i in range(n):
			for j in range(m):
				if b[i][j] != '*':
					if b[i][j] == 'U' and i > 0:
						dp[t][i][j] = dp[t - 1][i - 1][j]
					if b[i][j] == 'L' and j > 0:
						dp[t][i][j] = dp[t - 1][i][j - 1]
					if b[i][j] == 'D' and i < n - 1:
						dp[t][i][j] = dp[t - 1][i + 1][j]
					if b[i][j] == 'R' and j < m - 1:
						dp[t][i][j] = dp[t - 1][i][j + 1]
	for t in range(k):
		for i in range(n):
			for j in range(m):
				if b[i][j] == '*':
					return t
	return -1

def main():
	(n, m, k) = map(int, input().split())
	b = [input().strip() for _ in range(n)]
	print(solve(n, m, k, b))
