def g(n, dp, f, t):
	dp[0][0] = f
	for i in range(1, n):
		if t[i]:
			dp[0][i] = f
		else:
			dp[0][i] = dp[0][i - 1]
		dp[i][0] = dp[i - 1][0]
	for i in range(1, n):
		for j in range(1, n):
			dp[i][j] = dp[i - 1][j]
			if t[i]:
				dp[i][j] = max(dp[i][j], dp[i][j - 1])
		if t[i]:
			dp[i][0] = f
		else:
			dp[i][0] = dp[i - 1][0]
		for j in range(1, n):
			if t[j]:
				dp[i][j] = max(dp[i][j], dp[i][j - 1])
	return dp[n - 1][n - 1] == f

