def solve(m, n, k):
	dp = [0] * (k + 1)
	dp[0] = 1
	sum = 0
	for i in range(1, m + 1):
		sum += n
		for j in range(min(k, sum), 0, -1):
			dp[j] = dp[j] + dp[j - 1]
		for j in range(k + 1):
			if j < i:
				dp[j] = 0
			else:
				dp[j] = dp[j] - dp[j - i]
		dp[k] = dp[k] - dp[k - i]
	return dp[k]

