def cnt(i, j, k):
	if i > j:
		return 0
	if i == j:
		return 1
	mid = (i + j) // 2
	res = cnt(i, mid, k) + cnt(mid + 1, j, k)
	if k[mid] == '1':
		res += 1
	return res
