	dp = [[0] * (n * n + 1) for i in range(n * n + 1)]
	dp[0][0] = 1
	for i in range(1, n * n + 1):
		dp[i][i] = 1
		dp[i][i - 1] = 1
	for i in range(2, n * n + 1):
