def solve(s, k):
	counter = Counter(s)
	if k == 0:
		return 0
	elif k == 1:
		return len(s) - 1
	elif k == 2:
		return min(len(s) - 1, counter['a'] + 1)
	else:
		a = counter['a']
		b = counter['b']
		c = counter['c']
		return min(len(s) - 1, a + 2, b + c + 1)

