def cg(s, i, j):
	if i == j:
		return s[i]
	if s[i:j] == s[i] * (j - i):
		return s[i]
	k = (i + j) // 2
	if s[i:k] == s[i] * (k - i) and s[k:j] == s[k] * (j - k):
		return cg(s, k, j)
	if s[i:k] == s[i] * (k - i):
		return cg(s, i, k)
	if s[k:j] == s[k] * (j - k):
		return cg(s, k, j)
	return min(cg(s, i, k) + cg(s, k, j), cg(s, i, k) + cg(s, k, j) - 1)
