class Solution:

	def shortestPath(self, N, edges):
		g = [[] for _ in range(N)]
		for (u, v, w) in edges:
			g[u].append((v, w))
		dist = [float('inf')] * N
		dist[0] = 0
		q = [0]
		while q:
			u = q.pop()
			for (v, w) in g[u]:
				if dist[v] > dist[u] + w:
					dist[v] = dist[u] + w
					q.append(v)
		return dist[-1] if dist[-1] < float('inf') else -1

def shortestPath(n, edges):
	(graph, weight) = ([[0 for _ in range(n)] for _ in range(n)], [0 for _ in range(n)])
	for (s, e, w) in edges:
		graph[s][e] = 1
		weight[s] += w
		weight[e] -= w
	for i in range(n):
		if weight[i] != 0:
			return -1
	graph[0][n - 1] = 1
	weight[0] = weight[n - 1] = 0
	dp = [weight[0]] + [float('inf') for _ in range(n - 1)]
	for i in range(1, n):
		dp[i] = min(dp[j] + weight[i] for (j, x) in enumerate(graph[i]) if x)
	return dp[n - 1] if dp[n - 1] != float('inf') else -1


