def solve(n, p, c):
	e = [[] for _ in range(n + 1)]
	for i in range(1, n):
		e[p[i - 1]].append(i + 1)
	ans = 0
	for i in range(1, n + 1):
		e[i] = sorted(e[i], key=lambda x: -c[x - 1])
		e[i] = [0] + e[i]
		for j in range(1, len(e[i])):
			if e[i][j] == 0:
				break
			if e[i][j] == e[i][j - 1]:
				ans += 1
				e[i][j] = e[i][j - 1] + 1
	print(ans)

