from heapq import heappush, heappop

def solve(n, m):
	q = []
	heappush(q, (0, 0, 0))
	dist = [[0] * m for _ in range(n)]
	visited = [[0] * m for _ in range(n)]
	while q:
		(d, x, y) = heappop(q)
		if visited[x][y]:
			continue
		visited[x][y] = 1
		dist[x][y] = d
		if x + 1 < n:
			heappush(q, (d + 1, x + 1, y))
		if y + 1 < m:
			heappush(q, (d + 1, x, y + 1))
		if x + 1 < n and y + 1 < m:
			heappush(q, (d + 1, x + 1, y + 1))
		if x + 1 < n and y - 1 > -1:
			heappush(q, (d + 1, x + 1, y - 1))
		if y + 1 < m and x - 1 > -1:
			heappush(q, (d + 1, x - 1, y + 1))
		if y - 1 > -1 and x - 1 > -1:
			heappush(q, (d + 1, x - 1, y - 1))
		if y - 1 > -1:
			heappush(q, (d + 1, x, y - 1))
	return dist

