def solve(n, m, k):
	ans = [0] * (n * m + 1)
	for i in range(n):
		for j in range(m):
			for x in range(n):
				for y in range(m):
					if i == x or j == y:
						continue
					ans[max(abs(i - x), abs(j - y))] += 1
	return ans[:k + 1]


def check(n, m, k):
	ans = solve(n, m, k)
	for i in range(n * m + 1):
		if i % 2 == 1:
			continue
		tmp = solve(n, m, i)
		if tmp != ans:
			print('i=', i, 'ans=', ans, 'tmp=', tmp)
			return False
	return True


