class Input(object):

	def __init__(self):
		self.s = ''
		self.i = 0
		self.l = 0

	def read(self):
		self.s = input().strip()
		self.i = 0
		self.l = len(self.s)

	def next(self):
		while self.i < self.l and self.s[self.i] == ' ':
			self.i += 1
		j = self.i + 1
		while j < self.l and self.s[j] != ' ':
			j += 1
		ret = self.s[self.i:j]
		self.i = j
		return ret

	def nextInt(self):
		return int(self.next())

	def nextDouble(self):
		return float(self.next())

	def nextList(self):
		return list(map(int, self.next().split()))

	def nextIntList(self):
		return list(map(int, self.next().split()))

	def nextDoubleList(self):
		return list(map(float, self.next().split()))

	def nextCharList(self):
		return list(self.next())

	def nextIntMap(self):
		return {int(i): int(v) for (i, v) in enumerate(self.next().split())}

	def nextDoubleMap(self):
		return {int(i): float(v) for (i, v) in enumerate(self.next().split())}

	def nextCharMap(self):
		return {int(i): v for (i, v) in enumerate(self.next())}


