import operator

def solve(n, k, a):
	(x, i) = max(enumerate(a, 1), key=operator.itemgetter(1))
	if i > k:
		return (0, [])
	k -= i
	a = a[:x] + a[x + 1:]
	b = sorted(a)
	j = 0
	while j < len(b) and k >= b[j]:
		k -= b[j]
		j += 1
	return (j, [i] + [x for (x, i) in enumerate(a, 1)][:j])

def main():
	(n, k) = map(int, input().split())
	a = list(map(int, input().split()))
	(m, ans) = solve(n, k, a)
	print(m)
