def bisect(a, x, lo=0, hi=None):
	return bisect_right(a, x, lo)


def bisect_right(a, x, lo=0, hi=None):
	while lo < hi:
		mid = (lo + hi) // 2
		if a[mid] < x:
			lo = mid + 1
		else:
			hi = mid
	return hi


def bisect_left(a, x, lo=0, hi=None):
	while lo < hi:
		mid = (lo + hi) // 2
		if a[mid] < x:
			lo = mid + 1
		else:
			hi = mid
	return lo


