def check(x):
	return x + t > a[0][0]

(n, t) = list(map(int, input().split()))
l = [list(map(int, input().split())) for i in range(n)]
l.sort()
ans = 0
for (i, (x, a)) in enumerate(l):
	if t <= a:
		ans += 1
	if i > 0:
		ans += abs(x - l[i - 1][0]) // (t + a)
print(ans)

(n, t) = map(int, input().split())
