from heapq import heappush, heappop

def solve(n, m):
	q = []
	heappush(q, (0, 0, 0))
	dist = [[0] * m for _ in range(n)]
	used = [[0] * m for _ in range(n)]
	while q:
		(d, x, y) = heappop(q)
		if used[x][y]:
			continue
		used[x][y] = 1
		for (dx, dy) in [(1, 0), (-1, 0), (0, 1), (0, -1)]:
			nx = x + dx
			ny = y + dy
			if nx < 0 or nx >= n or ny < 0 or (ny >= m):
				continue
			if used[nx][ny]:
				continue
			if d + 1 > dist[nx][ny]:
				dist[nx][ny] = d + 1
				heappush(q, (d + 1, nx, ny))
	for i in range(n):
		for j in range(m):
			if i % 2 == 0:
				dist[i][j] += j
			else:
				dist[i][j] += m - j - 1
	print(*dist)
