class BIT:

	def __init__(self, n):
		self.size = n
		self.tree = [0] * (n + 1)

	def sum(self, i):
		s = 0
		while i > 0:
			s += self.tree[i]
			i -= i & -i
		return s

	def add(self, i, x):
		while i <= self.size:
			self.tree[i] += x
			i += i & -i

	def lower_bound(self, w):
		if w <= 0:
			return 0
		x = 0
		k = 1 << self.size.bit_length()
		for i in reversed(range(k)):
			if x + (1 << i) <= self.size and self.tree[x + (1 << i)] < w:
				w -= self.tree[x + (1 << i)]
				x += 1 << i
		return x + 1

	def __getitem__(self, i):
		return self.sum(i + 1) - self.sum(i)

	def __setitem__(self, i, x):
		self.add(i + 1, x - self[i])

	def __delitem__(self, i):
		self.add(i + 1, -self[i])

	def __len__(self):
		return self.size

	def __iter__(self):
		for i in range(self.size):
			yield self[i]

	def __reversed__(self):
		for i in reversed(range(self.size)):
			yield self[i]

	def __repr__(self):
		return '[' + ', '.join(map(repr, self)) + ']'

	def __str__(self):
		return str(list(self))

	def index(self, x):
		return self.lower_bound(x) - 1

	def count(self, x):
		i = self.lower_bound(x)
		if self[i] != x:
			return 0
		j = self.lower_bound(x + 1)
		return

