class BIT:

	def __init__(self, n):
		self.n = n
		self.bit = [0] * (n + 1)

	def add(self, i, x):
		while i <= self.n:
			self.bit[i] += x
			i += i & -i

	def sum(self, i):
		s = 0
		while i > 0:
			s += self.bit[i]
			i -= i & -i
		return s

	def range_sum(self, l, r):
		return self.sum(r) - self.sum(l - 1)

	def lower_bound(self, w):
		if w <= 0:
			return 0
		x = 0
		k = 1
		while k < self.n:
			k <<= 1
		l = 0
		r = self.n
		while r - l > 1:
			mid = (l + r) // 2
			if self.bit[mid] < w:
				l = mid
			else:
				r = mid
		return r

