def f(n, k):
	if n == 0:
		return 0.0
	if k == 0:
		return 1.0
	if n > k:
		return 0.0
	ans = 0.0
	l = 0
	r = k
	while l + 1 < r:
		m = (l + r) // 2
		x = (n + m - 1) // m
		y = (k + m - 1) // m
		if x < y:
			r = m
		else:
			ans = x
			l = m
	return ans

