from typing import List, Tuple

class Solution:

	def maxLength(self, arr: List[str]) -> int:
		dp = [set()]
		for s in arr:
			if len(set(s)) < len(s):
				continue
			dp += [c | {s} for c in dp if not (c & set(s))]
		return max((len(x) for x in dp))


