from bisect import bisect_left, bisect_right

def main():
	R = lambda : map(int, input().split())
	(T,) = R()
	for _ in range(T):
		(n, L) = R()
		a = sorted((R() for _ in range(n)))
		i = 0
		while i < n:
			j = bisect_right(a, (a[i][1] + L - 1, 0))
			if j == i:
				i += 1
			else:
				i = j
		print('No' if i == n else 'Yes')
main()

