import math

def digprod(n):
	prod = 1
	while n:
		prod *= n % 10
		n //= 10
	return prod

def g(n):
	if n < 10:
		return n
	else:
		return g(digprod(n))

def f(n):
	prod = 1
	while n:
		prod *= n % 10
		n //= 10
	return prod

def check(l, r, k):
	cnt = 0
	while l <= r:
		if g(l) == k:
			cnt += 1
		l += 1
	return cnt

def count(l, r, k):
	n = r - l + 1
	ones = math.floor(math.log10(l))
	digits = ones + 1
	lst = [0] * 10
	lst[0] = l - 1
	for i in range(1, 10):
		lst[i] = lst[i - 1] + 10 ** (ones - i)
	for i in range(1, digits + 1):
		if i % 9:
			lst[i] += n // 10 ** (digits - i)
		else:
			lst[i] += (n + 1) // 10 ** (digits - i)
	for i in range(1, 10):
		lst[0] += count(lst[i] + 1, lst[i] + f(lst[i] + 1), i)
	return lst[0]

def solve(l, r, k):
	ones = math.floor(math.log10(l))
	l -= 1
	return check(l, r, k) - count(l, r, k)

