				d[l[i]] = d[l[i - 1]]
			else:
				d[l[i]] = d[l[i - 1]] + 1
		d[l[-1]] = d[l[-1]] + 1
		r = sorted(list(d.values()), reverse=True)
		a = b = 0
		for i in range(len(r)):
			if r[i] >= 3:
				b = r[i]
				break
			a += r[i]
		if b:
			ans.append(2 * b - 3)
		elif a % 2 == 0:
			ans.append(2 * a - 2)
		else:
			ans.append(2 * a - 1)
	print('\n'.join(map(str, ans)))
