def solution(n, h, a):
	(l, r) = (0, n)
	while l + 1 < r:
		m = (l + r) // 2
		b = sorted(a[:m])
		i = 0
		for j in range(h):
			if i < m and j < b[i]:
				i += 1
		if i == m:
			l = m
		else:
			r = m
	return l


