def get_segments(lines):
	segments = []
	for (l, r) in lines:
		segments.append((l, 'L'))
		segments.append((r, 'R'))
	return sorted(segments)

def get_overlapping(segments):
	cur = 0
	max_overlapping = 0
	for (_, t) in segments:
		if t == 'L':
			cur += 1
		else:
			cur -= 1
		max_overlapping = max(max_overlapping, cur)
	return max_overlapping

def test():
	lines = [(1, 2), (2, 3), (3, 4), (1, 5), (2, 6)]
	segments = get_segments(lines)
	overlapping = get_overlapping(segments)
	print('Overlapping:', overlapping)

def solve(lines, L):
	segments = get_segments(lines)
	overlapping = get_overlapping(segments)
	if overlapping == L:
		return 'Yes'
	else:
		return 'No'

