		if h == house:
			continue
		distance = get_distance(h[0], h[1], house[0], house[1])
		x_diff = house[0] - h[0]
		y_diff = house[1] - h[1]
		if distance > t:
			continue
		if distance == t:
			if abs(x_diff) == t and abs(y_diff) == t:
				intersections.append(h)
				continue
			if abs(x_diff) == t:
				intersections.append((h[0], h[1] + y_diff / abs(y_diff) * t))
			if abs(y_diff) == t:
				intersections.append((h[0] + x_diff / abs(x_diff) * t, h[1]))
		else:
			if abs(x_diff) == t:
				intersections.append((h[0], h[1] + y_diff / abs(y_diff) * distance))
			if abs(y_diff) == t:
				intersections.append((h[0] + x_diff / abs(x_diff) * distance, h[1]))
